jQuery(document).ready(function($){function mceItemHight(){var values=[];item=$("#testimonials-carousel .carousel-item");item.each(function(index){var item_hight=$(this).height();values.push(item_hight)});values.sort();var value=values.pop()+25;item.css("min-height",value)}mceItemHight();function mceMenuScroll(target,height){var didScroll;var lastScrollTop=0;var delta=5;var targetHeight=height;$(window).scroll(function(event){didScroll=true});setInterval(function(){if(didScroll){hasScrolled();didScroll=false}},250);function hasScrolled(){var st=$(this).scrollTop();if(Math.abs(lastScrollTop-st)<=delta)return;if(st>lastScrollTop&&st>targetHeight){$(target).fadeOut()}else{if(st+$(window).height()<$(document).height()){$(target).fadeIn()}}lastScrollTop=st}}mceMenuScroll($("#top-menu"),$("#header").outerHeight());var gotoTop=$("#gototop");$(window).scroll(function(){if($("body,html").scrollTop()>Number($("#header").outerHeight())){$(gotoTop).fadeIn()}else{$(gotoTop).fadeOut()}});$(".scroll-to").click(function(e){e.preventDefault();var element=$(this),target=element.attr("data-target");$(window).scrollTo(target,{duration:500})});$(window).resize(function(){mceItemHight();mceMenuScroll($("#top-menu"),$("#header").outerHeight())})});