jQuery(document).ready(function($){function mce_fullscreen_hero(){$(".home #static-header-inner, .hero").css({width:$(window).width(),height:$(window).height()});$(".hero-header.bg-full").css({width:$(window).width(),"min-height":$(window).height()/1.5,height:"auto"});$(".hero-header.bg-empty").css({width:$(window).width(),"min-height":$(window).height()/2,height:"auto"})}mce_fullscreen_hero();function mce_item_hight(){var values=[];item=$("#testimonials-carousel .carousel-item");item.each(function(index){var item_hight=$(this).height();values.push(item_hight)});values.sort();var value=values.pop()+25;item.css("min-height",value)}mce_item_hight();function mce_menu_scroll(target,height){var didScroll;var lastScrollTop=0;var delta=5;var targetHeight=height;$(window).scroll(function(event){didScroll=true});setInterval(function(){if(didScroll){hasScrolled();didScroll=false}},250);function hasScrolled(){var st=$(this).scrollTop();if(Math.abs(lastScrollTop-st)<=delta)return;if(st>lastScrollTop&&st>targetHeight){$(target).fadeOut()}else{if(st+$(window).height()<$(document).height()){$(target).fadeIn()}}lastScrollTop=st}}mce_menu_scroll($("#top-menu"),$("#header").outerHeight());$().fancybox({selector:".gallery .gallery-item",infobar:false,smallBtn:false,closeExisting:true,loop:true,toolbar:true,buttons:["zoom","share","slideShow","fullScreen","download","thumbs","close"]});$(window).resize(function(){mce_fullscreen_hero();mce_item_hight();mce_menu_scroll($("#top-menu"),$("#header").outerHeight())})});